import React, { useState } from 'react';
import { createPost } from '../api';

const CreatePost = ({ token, fetchPosts, navigate }) => {

  const [ogTitle, setNewTitle] = useState('');
  const [ogDescription, setNewDesc] = useState('');
  const [ogLocation, setNewLocation] = useState('');
  const [ogPrice, setNewPrice] = useState('');
  const [ogwillDeliver, setNewWillDeliver] = useState(null);

  const newPost = {
    title: 'New Testers',
    description: 'testing 123',
    price: 'free',
    location: 'TX',
    willDeliver: true
  }

  async function addPost() {
    const results = await createPost(token, newPost)
    fetchPosts();
    navigate(`/posts`)
  }

  return (
    // This needs to be a form that accepts the 5 request parameters for creating a post
    <button onClick={() => addPost()}>Create a New Post</button>
  )
}

export default CreatePost;
